#' pretty_lists
#' 
#' A pretty list printer. Reduces extraneous space.
#' 
#' @param x a string
#' @return another string
#' 
#' @importFrom assertive assert_is_list
#' 
#' @noRd
#' @examples
#' pretty_lists()
pretty_lists <- function(x)
{
  assertive::assert_is_list(x)

   for(key in names(x)){
      value <- format(x[[key]])
      if(value == "") next
      cat(key, "=", value, "\n")
   }
   invisible(x)
}
